// src/App.js
import React from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
  Outlet,
  useLocation,
} from "react-router-dom";
import { Toaster } from "react-hot-toast";

import AdminDashboard from "./pages/admin/AdminDashboard";
import AdminProducts from "./pages/admin/AdminProducts";
import Navbar from "./components/Navbar";
import Home from "./pages/Home";
import Products from "./pages/Products"; // Customer homepage
import Checkout from "./pages/Checkout";
import Orders from "./pages/Orders";
import Notifications from "./pages/Notifications";
import Profile from "./pages/Profile";
import Login from "./pages/Login";
import Register from "./pages/Register";

import { useAuth } from "./context/AuthContext";
import { SidebarProvider } from "./context/SidebarContext";
import AdminSidebar from "./pages/admin/components/AdminSidebar";
import AdminTopbar from "./pages/admin/components/AdminTopbar";

// Loading screen component
function LoadingScreen() {
  return (
    <div className="min-h-screen bg-pink-50 flex items-center justify-center">
      <div className="text-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-[#B3611B] mx-auto mb-4"></div>
        <p className="text-gray-600">Loading...</p>
      </div>
    </div>
  );
}

// ProtectedRoute wrapper
function ProtectedRoute({ children }) {
  const { user, loading } = useAuth();
  if (loading) return null; // or a loading spinner
  if (!user) return <Navigate to="/login" replace />;
  return children ? children : <Outlet />;
}

// PublicRoute wrapper (prevents logged-in users from seeing login/register)
function PublicRoute({ children }) {
  return children ? children : <Outlet />;
}

// Admin-only route
function AdminRoute({ children }) {
  const { user, loading } = useAuth();
  if (loading) return null;
  if (!user || user.role !== "admin") {
    console.warn("❌ User is not admin. Redirecting to login.");
    return <Navigate to="/login" replace />;
  }
  return children ? children : <Outlet />;
}

// Customer-only route
function CustomerRoute({ children }) {
  const { user, loading } = useAuth();
  if (loading) return null;
  if (!user || user.role !== "customer") {
    console.warn("❌ User is not customer. Redirecting to login.");
    return <Navigate to="/login" replace />;
  }
  return children ? children : <Outlet />;
}

// Admin Layout wrapper
function AdminLayout() {
  return (
    <div className="flex h-screen bg-[#E6E6E6] overflow-hidden">
      <AdminSidebar />
      <div className="flex-1 flex flex-col min-w-0">
        <AdminTopbar />
        <div className="flex-1 overflow-y-auto">
          <Outlet />
        </div>
      </div>
    </div>
  );
}

export default function App() {
  const { user, loading } = useAuth();

  // Show loading screen while checking authentication
  if (loading) {
    return <LoadingScreen />;
  }

  return (
    <Router>
      <SidebarProvider>
        <AppContent />
        <Toaster
          position="top-center"
          toastOptions={{
            duration: 2500,
            style: { background: "#f472b6", color: "#fff", borderRadius: "10px" },
          }}
        />
      </SidebarProvider>
    </Router>
  );
}

// Main app content that lives inside the Router (so it can use useLocation)
function AppContent() {
  const { user } = useAuth();
  const location = useLocation();

  // Hide navbar on auth pages and for admin users
  const isAuthPage =
    location.pathname === "/login" || location.pathname === "/register";
  const showCustomerNavbar = user && user.role === "customer" && !isAuthPage;

  console.log("Current user role in App:", user?.role, "path:", location.pathname);

  return (
    <>
      {showCustomerNavbar && <Navbar />}

      <Routes>
        {/* Public routes */}
        <Route element={<PublicRoute />}>
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
        </Route>

        {/* Admin routes */}
        <Route element={<AdminRoute />}>
          <Route element={<AdminLayout />}>
            <Route path="/admin/dashboard" element={<AdminDashboard />} />
            <Route path="/admin/products" element={<AdminProducts />} />
          </Route>
        </Route>

        {/* Customer routes */}
        <Route element={<CustomerRoute />}>
          <Route path="/" element={<Products />} />
          <Route path="/products" element={<Products />} />
          <Route path="/checkout" element={<Checkout />} />
          <Route path="/orders" element={<Orders />} />
          <Route path="/profile" element={<Profile />} />
        </Route>

        {/* Fallback: redirect all unknown routes */}
        <Route
          path="*"
          element={
            user
              ? user.role === "admin"
                ? <Navigate to="/admin/dashboard" replace />
                : <Navigate to="/" replace />
              : <Navigate to="/login" replace />
          }
        />
      </Routes>
    </>
  );
}
