// src/App.js
import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate, Outlet } from "react-router-dom";
import { Toaster } from "react-hot-toast";

import AdminDashboard from "./pages/admin/AdminDashboard";
import AdminProducts from "./pages/admin/AdminProducts";
import Navbar from "./components/Navbar";
import Home from "./pages/Home";
import Products from "./pages/Products"; // Customer homepage
import Checkout from "./pages/Checkout";
import Orders from "./pages/Orders";
import Notifications from "./pages/Notifications";
import Profile from "./pages/Profile";
import Login from "./pages/Login";
import Register from "./pages/Register";

import { CartProvider } from "./context/CartContext";
import { AuthProvider, useAuth } from "./context/AuthContext";
import { SidebarProvider } from "./context/SidebarContext";

// Loading screen component
function LoadingScreen() {
  return (
    <div className="min-h-screen bg-pink-50 flex items-center justify-center">
      <div className="text-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-[#B3611B] mx-auto mb-4"></div>
        <p className="text-gray-600">Loading...</p>
      </div>
    </div>
  );
}

// ProtectedRoute wrapper
function ProtectedRoute({ children }) {
  const { user, loading } = useAuth();
  if (loading) return null; // or a loading spinner
  if (!user) return <Navigate to="/login" replace />;
  return children ? children : <Outlet />;
}

// PublicRoute wrapper (prevents logged-in users from seeing login/register)
function PublicRoute({ children }) {
  const { user, loading } = useAuth();
  if (loading) return null; // or a loading spinner
  if (user) {
    // Role-based redirect
    if (user.role === "admin") return <Navigate to="/admin/dashboard" replace />;
    if (user.role === "customer") return <Navigate to="/customer/home" replace />;
    return <Navigate to="/" replace />;
  }
  return children ? children : <Outlet />;
}

// Admin-only route
function AdminRoute({ children }) {
  const { user } = useAuth();
  if (!user || user.role !== "admin") {
    return <Navigate to="/login" replace />;
  }
  return children ? children : <Outlet />;
}

// Customer-only route
function CustomerRoute({ children }) {
  const { user, loading } = useAuth();
  if (loading) return null;
  if (!user || user.role !== "customer") {
    return <Navigate to="/login" replace />;
  }
  return children ? children : <Outlet />;
}

export default function App() {
  const { user, loading } = useAuth();

  // Show loading screen while checking authentication
  if (loading) {
    return <LoadingScreen />;
  }

  return (
    <AuthProvider>
      <CartProvider>
        <Router>
          <SidebarProvider>
            {/* Show Navbar only if authenticated */}
            {user && <Navbar />}

            <Routes>
              {/* Public routes */}
              <Route element={<PublicRoute />}>
                <Route path="/login" element={<Login />} />
                <Route path="/register" element={<Register />} />
              </Route>

              {/* Admin routes */}
              <Route element={<AdminRoute />}>
                <Route path="/admin/dashboard" element={<AdminDashboard />} />
                <Route path="/admin/products" element={<AdminProducts />} />
                {/* ...other admin routes... */}
              </Route>

              {/* Customer routes */}
              <Route element={<CustomerRoute />}>
                <Route path="/" element={<Products />} />
                <Route path="/products" element={<Products />} />
                <Route path="/checkout" element={<Checkout />} />
                <Route path="/orders" element={<Orders />} />
                <Route path="/profile" element={<Profile />} />
                {/* ...other customer routes... */}
              </Route>

              {/* Fallback: redirect all unknown routes */}
              <Route path="*" element={<Navigate to={user ? (user.role === "admin" ? "/admin/dashboard" : "/") : "/login"} replace />} />
            </Routes>
          </SidebarProvider>
          <Toaster
            position="top-center"
            toastOptions={{
              duration: 2500,
              style: { background: "#f472b6", color: "#fff", borderRadius: "10px" },
            }}
          />
        </Router>
      </CartProvider>
    </AuthProvider>
  );
}
